type TouchableButtonProps = PressableProps & {
  scale?: number;
  style?: StyleProp<ViewStyle>;
};

const PressableScale: React.FC<TouchableButtonProps> = ({
  scale,
  style,
  children,
  ...otherProps
}) => {
  return (
    <Pressable
      style={({ pressed }) => [
        style,
        { transform: [{ scale: pressed ? scale ?? 0.95 : 1 }] },
      ]}
      {...otherProps}
    >
      {children}
    </Pressable>
  );
};

function AddButton() {
  const navigation = useNavigation();

  return (
    <View style={styles.container}>
      <SvgContainer />

      <PressableScale
        style={styles.circularButton}
        onPress={() => {
          navigation.navigate("NewSubscription");
        }}
      >
        <View>
          <Image
            source={require("../assets/images/plus.png")}
            style={styles.circularButtonImage}
          />
        </View>
      </PressableScale>
    </View>
  );
}

